INSERT INTO `diplomas` (`id`, `user_id`, `faculty_id`, `location`, `mentor`, `dcompany`, `title`, `description`, `results`, `requirements`, `published`, `status`, `created_at`) VALUES
(52, 68, 17, 'Алматы', 'Николаев Виталий Евгеньевич', 'ТОО «Адал-метео»', 'Разработка интернет-портала, для работы с метеорологической и экологической информацией', 'Дипломный проект предусматривает разработку веб-приложения для работы с информационной системой по сбору, обработке и хранению метеорологической и экологической информации.', 'В результате выполнения дипломного проекта должен быть реализован интернет портал для работы с метеорологической и экологической информацией.', 'Студент должен обладать базовыми и специальными компетенциями по специальности ВТиПО', 1, '', '2022-05-23 09:27:55'),
(54, 68, 17, 'Алматы', 'Николаев Виталий Евгеньевич', 'ТОО «Адал-метео»', 'Разработка информационной системы для сбора, обработки, анализа и хранения метеорологических и экологических данных', 'Дипломный проект предусматривает разработку информационной системы, которая включает в себя свободно распространяемую базу данных, модуль приема данных, модули обработки, анализа и записи результатов в базу данных. ', 'В результате выполнения дипломного проекта должна быть реализована информационная система для сбора, обработки, анализа и хранения метеорологических и экологических данных', 'Студент должен обладать базовыми и специальными компетенциями по специальности ВТиПО', 1, '', '2022-05-23 09:30:40'),
(56, 69, 14, 'Нур-Султан', 'По прибытии в компанию', 'ТОО «NTS Design»', 'Разработка аппаратной части устройства потоковой трансляции аудиосигнала с цифрового MEMS микрофона по сети Ethernet на базе микроконтроллеров STM32.', 'Цифровое устройство, подключаемое к сети Ethernet и транслирующее аудиоинформацию.\r\nУстройство должно быть выполнено в корпусе (материал по выбору студента). Ожидается, что в данном устройстве разработка схемотехники будет осуществлена с нуля или на основе аналогов, при этом печатная плата полностью разработана с учетом особенностей работы в сети Ethernet и работы MEMS микрофонов. Также ожидается, что студент доведет устройство до полностью функционального опытного образца изготовленного на базе предприятия.\r\n', 'Знание схемотехники, законов Ома, Кирхгофа. Умение осуществлять расчет электрических цепей в том числе с учетом требуемой скорости передачи данных. Умение осуществлять проектирование печатных плат. Знание и навыки работы в специальных средствах автоматизирования проектирования (Altium Designer) и симуляции работы электрических цепей (LTSpice)\r\nВ настоящее время кандидатура для данного дипломного проекта на 2021-2022 учебный год имеется.\r\n', 'Разработка аппаратной части устройства потоковой трансляции аудиосигнала с цифрового MEMS микрофона по сети Ethernet на базе микроконтроллеров STM32', 1, '', '2022-05-23 17:18:04'),
(57, 69, 14, 'Нур-Султан', 'По прибытии в компанию', 'ТОО «NTS Design»', 'Разработка программной части устройства потоковой трансляции аудиосигнала с цифрового MEMS микрофона по сети Ethernet на базе микроконтроллеров STM32.', 'Цифровое устройство, подключаемое к сети Ethernet и транслирующее аудиоинформацию (например, по протоколу RTSP) на соответствующий сервер. При этом изделие настраивается посредством программного обеспечения и/или WEB-интерфейса. Потоковая передача должна осуществляться непрерывно. При обрывах соединения или перезагрузки устройство должно автоматически восстанавливать потоковую передачу. В программной реализации ожидается также набор программного обеспечения для полной реализации устройства, структурная схема интерфейсов и базовый алгоритм работы. MEMS микрофон должен быть обязательно с цифровым интерфейсом.', 'Знание языка программирования Си. Опыт разработки программного обеспечения для микроконтроллеров STM32. Знания и навыки работы с протоколами SPI, UART, I2C, I2S, PDM.\r\nВ настоящее время кандидатура для данного дипломного проекта на 2021-2022 учебный год имеется.\r\n', 'Разработка программной части устройства потоковой трансляции аудиосигнала с цифрового MEMS микрофона по сети Ethernet на базе микроконтроллеров STM32.', 1, '', '2022-05-23 17:19:12'),
(58, 69, 15, 'Нур-Султан', 'По прибытии в компанию', 'ТОО «NTS Design»', 'Разработка QPSK/QAM демодулятора', 'Рабочий QPSK/QAM модулятор способный принимать цифровые данные с микроконтроллера/микропроцессора и обеспечивать передачу сигналов в радиоэфир в собственном протоколе. Предполагается что разработка будет осуещствлена на основе дискретных элементов (не готовой интегральной схемой)', 'Знание основ аналоговой и цифровой схемотехники. Умение пользоваться прикладными САПР для разработки и моделирования. Умение вести расчеты ВЧ схемотехники. Умение пользоваться контрольно-измерительным оборудованием', 'Разработка QPSK/QAM модулятора\r\n\r\n', 1, '', '2022-05-23 17:19:51'),
(59, 69, 15, 'Нур-Султан', 'По прибытии в компанию', 'ТОО «NTS Design»', 'Разработка QPSK/QAM модулятора', 'Рабочий QPSK/QAM модулятор способный принимать цифровые данные с микроконтроллера/микропроцессора и обеспечивать передачу сигналов в радиоэфир в собственном протоколе. Предполагается что разработка будет осуещствлена на основе дискретных элементов (не готовой интегральной схемой)', 'Знание основ аналоговой и цифровой схемотехники. Умение пользоваться прикладными САПР для разработки и моделирования. Умение вести расчеты ВЧ схемотехники. Умение пользоваться контрольно-измерительным оборудованием', 'Разработка QPSK/QAM модулятора', 1, '', '2022-05-23 17:20:28'),
(60, 70, 17, 'Петропавлоск', 'Лысенко С.П.', 'АО «Завод имени С.М. Кирова»', 'Автоматизации бизнес-процессов компании', '', 'Автоматизация бизнес-процессов компании', 'Аналитические способности, экономические знания, знания программирования начального уровня.', 1, '', '2022-05-23 17:21:41'),
(61, 70, 11, 'Петропавлоск', 'Лысенко С.П.', 'АО «Завод имени С.М. Кирова»', 'Разработка модели комплексной системы защиты информации на предприятии', '', 'Разработка рекомендаций, комплекса инженерно-технических и программно-аппаратных средств по обеспечению защиты информации на машиностроительном (радиотехническом) предприятии', 'Аналитические способности, технические знания, знания программирования начального уровня. Администрирование серверов и рабочих станций.', 1, '', '2022-05-23 17:22:12'),
(62, 70, 11, 'Петропавлоск', 'Лысенко С.П.', 'АО «Завод имени С.М. Кирова»', 'Интеллектуальные системы видеонаблюдения на производстве;', '', 'Анализ современных цифровых  систем видеонаблюдения, их преимущества перед аналоговыми, применимость в условиях производства, экономический эффект от внедрения, перспективы развития.', 'Аналитические способности, технические знания, знания программирования начального уровня. Администрирование серверов и рабочих станций. ', 1, '', '2022-05-23 17:22:49'),
